   , c as (
    insert into category (name, number, "shortName")
        select distinct "categoryName" as name, "categoryNumber" as number, "shortName"
        from data
        order by number
        on conflict ("number") do nothing
        returning id, number
)
   , q as (
    insert into question (name, "longForm", number, "categoryId", description)
        select distinct "questionName" as name, "longForm", "questionNumber" as number, (select id from c where c.number = "categoryNumber") as "categoryId", "questionDescription" as description
        from data
        order by number
        returning id, name
)
   , r as (
    insert into resource (description, name, type, url)
        select distinct "resourceDescription" as description, "resourceName" as name, type, url
        from data
        where url is not null
        returning id, url
)
insert into question_resources_resource ("questionId", "resourceId")
select
    q.id as "questionId",
    r.id as "resourceId"
from q
         join data d on q.name = d."questionName"
         join r on d.url = r.url;

-- Populates a sample assessment and associates it with the user with the email address specified in the constants CTE
with constants as (
    select
        '2024-03-19 02:47:43.000000' as "createdAt",
        '8adc82fb-5d82-4804-ad82-3b82a38fe67c' as "rscId",
        'micro_to_small' as type,
        'user@mail.com'  as email -- Change 'user@mail.com' to the email address of the user you want to associate the assessment with
)
   ,data(name, selection) as (
        values
            ('u_it_org_size', 'micro_to_small'),
