   , u as (
    select id, c.email
    from "user"
             cross join constants c
    where "user".email = c.email
    limit 1
)
   , a as (
    insert into assessment ("createdAt", "type", "rscId", "userId")
        select distinct
            c."createdAt"::timestamp,
            c.type,
            c."rscId",
            u.id
        from data
                 cross join constants c
                 join u on u.email = c.email
        returning id, "rscId"
)
   , q as (
    select id, data.name
    from question
             join data on question.name = data.name
    where question.name = data.name
)
insert into response ("selection", "assessmentId", "questionId")
select selection, a.id as "assessmentId", q.id as "questionId"
from data
         join a on a."rscId" = (select "rscId" from constants)
         join q on q.name = data.name
         cross join constants c
         join u on u.email = c.email;
